<js>
result.toArray().slice(0,5).map(o=>{
    var detail = o.select('.book_name').attr('href');
    var J = org.jsoup.Jsoup.parse(java.ajax(detail));
    return {
       name:  o.select('.book_name').text(),
       author:o.select('.book_aut').text(),
       tags:  String(o.select('.book_inf_sec .label').text()).replace('中','').replace('已',''),
       num: o.select('.book_inf_right p:eq(0)').text(),
       latest:J.select('.update_chapter').text(),
       intro: String(o.select('.book_int').text()).trim(),
       cover: o.select('.book_cov img').attr('src'),
       url:detail,
    }
});
</js>

